mapper.select:
SELECT event_num, admin_num, event_title, event_sub_title, event_img1, event_img2, event_msg, event_start_date, event_end_date, event_date, event_del_flag FROM ( SELECT ROW_NUMBER() OVER (ORDER BY event_num DESC) rn, e.* FROM event e WHERE e.event_del_flag = 'N'
AND e.event_title LIKE '%' || #{keyword} || '%'
AND e.event_msg LIKE '%' || #{keyword} || '%'
AND e.event_sub_title LIKE '%' || #{keyword} || '%'
) WHERE rn BETWEEN #{startNum} AND #{endNum}

mapper.update:
UPDATE event
SET
  event_del_flag = 'Y',
  admin_num = #{adminNum}
WHERE event_num = #{eventNum}
  AND event_del_flag = 'N'
