mapper.select:
select num, title, input_date,cnt,id
from ( select num, title, content,
ip, cnt, id,input_date,row_number() over(order by input_date desc)
rnum
from board


		instr( ${fieldStr} , #{keyword})!=0


) where rnum between #{startNum} and #{endNum}

select title, content, input_date, ip, cnt, id
from board
where num = #{num}

mapper.update:
update board
set cnt = cnt+1
where num = #{num}

update board
set title=#{title}, content=#{content}, ip=#{ip}
where num=#{num} and id=#{id}
