mapper.select:
select member_pass
from member 
where member_id =#{memberid}

select member_last_name, member_first_name, member_email, member_tel, member_birth
from member
where member_id =#{memberId}

mapper.update:
update member
set member_email = #{memberEmail},
    member_tel   = #{memberTel},
    member_birth = #{memberBirth}
where member_id = #{memberId}
