mapper.select:
select noticeNum, noticeTitle, noticeCategory, noticeDate, noticeMsg, adminNum from ( select notice_num as noticeNum, admin_num as adminNum, notice_title as noticeTitle, notice_category as noticeCategory, notice_msg as noticeMsg, notice_date as noticeDate, row_number() over(order by notice_date desc) rnum from notice
notice_del_flag = 'N'
and notice_category = '일반공지' and notice_category = '멤버십' and notice_category = '약관개정'
and instr(notice_title, #{keyword}) != 0
) where rnum between #{startNum} and #{endNum}

mapper.insert:
insert into notice (
  notice_num, admin_num, notice_title, notice_category, notice_msg, notice_date, notice_del_flag
) values (
  seq_notice.nextval, #{adminNum}, #{noticeTitle}, #{noticeCategory}, #{noticeMsg}, sysdate, 'N'
)

mapper.update:
update notice
set
  notice_title = #{noticeTitle},
  notice_category = #{noticeCategory},
  notice_msg = #{noticeMsg}
where notice_num = #{noticeNum}

update notice
set notice_del_flag = 'Y'
where notice_num = #{value}
