mapper.select:
SELECT COUNT(*) FROM reserve r, member m, pay p, pay_info pi WHERE m.member_num = r.member_num AND r.reserve_num = p.reserve_num AND p.pay_num = pi.pay_num AND r.member_num IS NOT NULL AND r.non_user_num IS NULL AND r.reserve_type = 'room'
AND (r.END_USER_LAST_NAME || r.END_USER_FIRST_NAME) LIKE '%' || #{keyword} || '%'
AND r.reserve_num = #{keyword}
AND m.member_id LIKE '%' || #{keyword} || '%'
AND ( (r.END_USER_LAST_NAME || r.END_USER_FIRST_NAME) LIKE '%' || #{keyword} || '%' OR m.member_id LIKE '%' || #{keyword} || '%' OR (REGEXP_LIKE(#{keyword}, '^[0-9]+$') AND r.reserve_num = TO_NUMBER(#{keyword}))
)

SELECT * FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY r.reserve_status ASC, CASE WHEN r.reserve_start_date >= TRUNC(SYSDATE) THEN 0 ELSE 1 END ASC, r.reserve_start_date ASC, r.reserve_time DESC ) AS rnum, r.reserve_num AS reserveNum, r.end_user_last_name || r.end_user_first_name AS reserveName, m.member_id AS memberId, TO_CHAR(r.reserve_time,'YYYY-MM-DD') AS reserveTime, p.pay_price AS totalPrice, TO_CHAR(r.reserve_start_date,'YYYY-MM-DD') AS startDate, TO_CHAR(r.reserve_end_date,'YYYY-MM-DD') AS endDate, TO_CHAR(pi.input_date,'YYYY-MM-DD HH24:MI') AS inputDate, r.reserve_status AS reserveFlag FROM reserve r, member m, pay p, pay_info pi WHERE m.member_num = r.member_num AND r.reserve_num = p.reserve_num AND p.pay_num = pi.pay_num AND r.member_num IS NOT NULL AND r.non_user_num IS NULL AND r.reserve_type = 'room'
AND (r.END_USER_LAST_NAME || r.END_USER_FIRST_NAME) LIKE '%' || #{keyword} || '%'
AND r.reserve_num = #{keyword}
AND m.member_id LIKE '%' || #{keyword} || '%'
AND ( (r.END_USER_LAST_NAME || r.END_USER_FIRST_NAME) LIKE '%' || #{keyword} || '%' OR m.member_id LIKE '%' || #{keyword} || '%' OR (REGEXP_LIKE(#{keyword}, '^[0-9]+$') AND r.reserve_num = TO_NUMBER(#{keyword}))
)
) WHERE rnum BETWEEN #{startNum} AND #{endNum}
