mapper.select:
select member_id, member_email
from member
where member_id = #{memberId}
  and member_last_name = #{memberLastName}
  and member_first_name = #{memberFirstName}
  and member_del_flag = 'N' -- 탈퇴하지 않은 회원만 대상

mapper.update:
UPDATE member
SET member_pass = #{newPw}
WHERE member_Id = #{memberId}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE member_id = #{memberId}
