mapper.select:
		 SELECT MEMBER_LAST_NAME || MEMBER_FIRST_NAME
 		 FROM member
 		 WHERE member_id = #{memberId}

select count(*) from member m, reserve r where r.member_num=m.member_num and r.RESERVE_TYPE='room' and r.reserve_status='N'
AND r.reserve_start_date >= TRUNC(SYSDATE) AND r.reserve_start_date < ADD_MONTHS(TRUNC(SYSDATE), 3)
and m.member_id=#{memberId}

SELECT member_pass
FROM member 
WHERE member_id = #{memberId}

mapper.update:
UPDATE member
SET MEMBER_DEL_FLAG = 'Y'
WHERE MEMBER_ID = #{memberId}
